client_script:
// Load Facebook SDK asynchronously
(function (d, s, id) {
  var js,
    fjs = d.getElementsByTagName(s)[0];
  if (d.getElementById(id)) return;
  js = d.createElement(s);
  js.id = id;
  js.src = "https://connect.facebook.net/en_US/sdk.js";
  fjs.parentNode.insertBefore(js, fjs);
})(document, "script", "facebook-jssdk");

window.fbAsyncInit = function () {
  FB.init({
    appId: "1692370301463791", // your app id
    autoLogAppEvents: true,
    xfbml: true,
    version: "v23.0",
  });
};

let email = "";
let phone_id = "";
let business_id = "";
let business_name = "";
let code = "";
let with_mobile_app = false;

// Handle messages from Embedded Signup iFrame
window.addEventListener("message", (event) => {
  if (
    event.origin !== "https://www.facebook.com" &&
    event.origin !== "https://web.facebook.com"
  ) {
    return;
  }
  try {
    const data = JSON.parse(event.data);
    console.log("WHATSAPP DATA: " + data);
    if (data.type === "WA_EMBEDDED_SIGNUP") {
      if (data.event === "FINISH" || data.event === "FINISH_WHATSAPP_BUSINESS_APP_ONBOARDING") {
        const { phone_number_id, waba_id } = data.data;
        phone_id = phone_number_id;
        business_id = waba_id;
        
        if (data.event === "FINISH_WHATSAPP_BUSINESS_APP_ONBOARDING") {
            with_mobile_app = true;
        }
        
      } else if (data.event === "CANCEL") {
        const { current_step } = data.data;
        console.warn("Cancel at ", current_step);
      } else if (data.event === "ERROR") {
        const { error_message } = data.data;
        console.error("error ", error_message);
      }
    }
    document.getElementById("session-info-response").textContent =
      JSON.stringify(data, null, 2);
  } catch {
    console.log("Non JSON Responses", event.data);
  }
});
  // Handle button click
  document.getElementById("signup-btn").addEventListener("click", function () {
    email = $('[data-fieldname="user"] input').val();
    business_name = $('[data-fieldname="business_name"] input').val();
 
    if (!email) {
      frappe.msgprint("Please enter your email before signing up.");
      return;
    }
    
    if (!business_name) {
      frappe.msgprint("Please enter your business name before signing up.");
      return;
    }

    // Launch WhatsApp/Facebook Embedded Signup
    FB.login(fbLoginCallback, {
      config_id: "1993600688075447", // your config_id from Meta
      response_type: "code",
      override_default_response_type: true,
      extras: {
        version: "v3",
        featureType: "whatsapp_business_app_onboarding",
      },
    });
  });

// Callback function after login/signup
const fbLoginCallback = (response) => {
  if (response.authResponse) {
    const code = response.authResponse.code;
    const body = {
        code: code,
        phone_id: phone_id,
        business_id: business_id,
        business_name: business_name,
        email: email,
        with_mobile_app: with_mobile_app
    }
    console.log(body)
    const url =
      "/api/method/whatsapp_integration.whatsapp_onboarding_api.init_instance";
    fetch(url, {
      method: "POST",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json",
        "X-Frappe-CSRF-Token": (window.frappe && frappe.csrf_token) || ""
      },
      body: JSON.stringify(body)
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error(`HTTP error! Status: ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        console.log("Server response:", data);
        if(!data.message.error) {
            console.log(data)
            frappe.msgprint("WhatsApp Instance is created successfully");
            const instance_id = data.message.instance_id;
            location.href = `/app/whatsapp-instance/${instance_id}`;
        }
      })
      .catch((err) => {
        console.error("Fetch error:", err);
      });
  }
};
